import React, { Component } from 'react';
import { dateToString } from '../utils';
import EventCard from '../components/EventCard';
import Button from '../components/Button';

class Day extends Component {
    state = {
        events: [
            {
                date: new Date(),
                description: 'lalalala',
            },
            {
                date: new Date(),
                description: 'lelelele',
            },
            {
                date: new Date(),
                description: 'lilililil',
            },
        ],
    };

    render() {
        const { switchView, currDate } = this.props;
        const events = this.state.events.map((el, i) => (
            <EventCard key={i} date={el.date} description={el.description} />
        ));
        return (
            <section className="day-view">
                <h1 className="section-title">Events on {dateToString(currDate)}</h1>
                <div className="day-view__events-box">{events}</div>
                <div className="day-view__btns">
                    <Button>Add New</Button>
                    <Button onClick={switchView('home', currDate)}>Back</Button>
                </div>
            </section>
        );
    }
}

export default Day;
