import React, { Component } from 'react';

const MONTH_NAMES = [
    'January',
    'February',
    'March',
    'April',
    'May',
    'June',
    'July',
    'August',
    'September',
    'October',
    'November',
    'December',
];

export class Calendar extends Component {
    state = {
        currDate: this.props.currDate,
        daysWithEvents: [],
    };

    getMonthStringFromDate = (date) => {
        return MONTH_NAMES[date.getMonth()];
    };

    generateDays = (date) => {
        const days = [];
        const currDate = new Date(this.state.currDate);
        currDate.setDate(1);
        // Starting from some days from previous month:
        while (currDate.getDay() !== 1) currDate.setDate(currDate.getDate() - 1);

        // Generating the days:
        const daysRows = 6;
        const daysInWeek = 7;

        let dayCounter = 0;
        while (dayCounter < daysRows * daysInWeek) {
            days.push({
                date: currDate.getDate(),
                active: currDate.getMonth() === this.state.currDate.getMonth(),
            });
            currDate.setDate(currDate.getDate() + 1);
            dayCounter++;
        }
        return days;
    };

    onDayClicked = (e) => {
        const date = this.state.currDate;
        date.setDate(e.target.innerText);
        if (e.target.classList.length > 1) {
        }
    };

    onMonthChanged = (offset) => {
        const date = new Date(this.state.currDate);
        let newMonth = date.getMonth() + offset;
        if (newMonth > 11) {
            newMonth = 0;
            date.setFullYear(date.getFullYear() + 1);
        }
        if (newMonth < 0) {
            newMonth = 11;
            date.setFullYear(date.getFullYear() - 1);
        }
        date.setMonth(newMonth);

        this.setState({
            currDate: date,
        });
    };

    render() {
        const days = this.generateDays(this.state.currDate).map(({ date, active }, i) => (
            <div
                key={i}
                className={`calendar__day ${!active ? 'calendar__day--ghost' : ''}`}
                onClick={this.onDayClicked}
            >
                {date}
            </div>
        ));

        return (
            <div className="calendar">
                <div className="calendar__navbar">
                    <div className="calendar__arrow" onClick={() => this.onMonthChanged(-1)}>
                        L
                    </div>
                    <h1 className="calendar__title">
                        {this.getMonthStringFromDate(this.state.currDate)}
                    </h1>
                    <div className="calendar__arrow" onClick={() => this.onMonthChanged(1)}>
                        R
                    </div>
                </div>
                <div className="calendar__days-box">{days}</div>
            </div>
        );
    }
}

export default Calendar;
