import React, { useState } from 'react';
import { timeToString } from '../utils';

const TimeInput = (props) => {
    const [hours, setHours] = useState(0);
    const [minutes, setMinutes] = useState(0);

    const addToTimeValue = (time, addValue, limit) => {
        const newTime = time + addValue;
        if (newTime > limit) return newTime - limit;
        if (newTime < 0) return limit + newTime;

        return newTime;
    };

    return (
        <div className="time-input">
            <h3 className="time-input__label">{props.label}</h3>
            <div className="time-input__time-box">
                <div className="time-input__input">{timeToString(hours)}</div>
                <div className="time-input__btns-box">
                    <button
                        type="button"
                        className="time-input__btn"
                        onClick={() => setHours(addToTimeValue(hours, 1, 11))}
                    >
                        ^
                    </button>
                    <button type="button" className="time-input__btn">
                        ^
                    </button>
                </div>
                <div className="time-input__input">{timeToString(minutes)}</div>
                <div className="time-input__btns-box">
                    <button type="button" className="time-input__btn">
                        ^
                    </button>
                    <button type="button" className="time-input__btn">
                        ^
                    </button>
                </div>
            </div>
        </div>
    );
};

export default TimeInput;
