import React, { Component } from 'react';
import './assets/scss/main.scss';
import Home from './views/Home';
import Event from './views/Event';
import Day from './views/Day';

class App extends Component {
    state = {
        currView: 'home',
        currDate: new Date(),
    };

    componentDidMount() {
        // const xhttp = new XMLHttpRequest();
        // xhttp.open('POST', 'http://localhost:3000/event/add', true);
        // xhttp.setRequestHeader('Content-type', 'application/json');
        // xhttp.setRequestHeader('Access-Control-Allow-Origin', 'origin');
        // xhttp.send({
        //     year: 2020,
        //     month: 12,
        //     day: 12,
        //     hours: 12,
        //     minutes: 12,
        //     description: 'aaaaa',
        // });
    }

    toggleView = (viewName, date) => {
        this.setState({ currView: viewName, currDate: date });
    };

    render() {
        let renderView;
        switch (this.state.currView) {
            case 'home':
                renderView = <Home switchView={this.toggleView} currDate={this.state.currDate} />;
                break;
            case 'day':
                renderView = <Day switchView={this.toggleView} currDate={this.state.currDate} />;
                break;
            case 'event':
                renderView = <Event switchView={this.toggleView} currDate={this.state.currDate} />;
                break;
            default:
                renderView = <Home switchView={this.toggleView} currDate={this.state.currDate} />;
                break;
        }
        return (
            <div className="App">
                {renderView}
                <Footer />
            </div>
        );
    }
}

export default App;
